mapper.select:
select 

from friend
where uid = #{uid,jdbcType=BIGINT}
  and fuid = #{fuid,jdbcType=BIGINT}

mapper.delete:
delete from friend
where uid = #{uid,jdbcType=BIGINT}
  and fuid = #{fuid,jdbcType=BIGINT}

mapper.update:
update friend


    group_id = #{groupId,jdbcType=BIGINT},


    c_time = #{cTime,jdbcType=TIMESTAMP},


where uid = #{uid,jdbcType=BIGINT}
  and fuid = #{fuid,jdbcType=BIGINT}

update friend
set group_id = #{groupId,jdbcType=BIGINT},
  c_time = #{cTime,jdbcType=TIMESTAMP}
where uid = #{uid,jdbcType=BIGINT}
  and fuid = #{fuid,jdbcType=BIGINT}
