mapper.select:
select 

from friend
where uid = #{uid,jdbcType=INTEGER}
  and fuid = #{fuid,jdbcType=INTEGER}

mapper.delete:
delete from friend
where uid = #{uid,jdbcType=INTEGER}
  and fuid = #{fuid,jdbcType=INTEGER}

mapper.update:
update friend


    group_id = #{groupId,jdbcType=INTEGER},


    c_time = #{cTime,jdbcType=TIMESTAMP},


where uid = #{uid,jdbcType=INTEGER}
  and fuid = #{fuid,jdbcType=INTEGER}

update friend
set group_id = #{groupId,jdbcType=INTEGER},
  c_time = #{cTime,jdbcType=TIMESTAMP}
where uid = #{uid,jdbcType=INTEGER}
  and fuid = #{fuid,jdbcType=INTEGER}

